import 'package:flutter/material.dart';
import './models/location.dart';
import 'second_page.dart'; 


class LocalDetail extends StatelessWidget {
  final Location locationData;

  LocalDetail(this.locationData);

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text(locationData.name),
        backgroundColor: Colors.blue,
      ),
      body: Column(
        mainAxisAlignment: MainAxisAlignment.center,
        crossAxisAlignment: CrossAxisAlignment.center,
        children: [
          section("Rafi", Colors.cyan),
          section("tanu", Colors.green),
          SizedBox(height: 20), // Add some space between the sections and the button
          ElevatedButton(
            onPressed: () {
              // Navigate to the second page when the button is pressed
               Navigator.push(
                context,
                MaterialPageRoute(
                  builder: (context) => SecondPage(
                    'data:image/jpeg;base64,', // Replace with your image URL
                  ),
                ),
              );
            },
            child: Text('Show Image'),
          ),
        ],
      ),
    );
  }

  Widget section(String title, Color col) {
    return Container(
      decoration: BoxDecoration(color: col),
      child: Text(title),
    );
  }
}

